function convert(str, val){
	var re='',ss=val[0],tt=val[1];
	for(var i=0;i<str.length;i++){
		var nowStr = str.charAt(i);
		if(str.charCodeAt(i)>10000&&ss.indexOf(nowStr)!=-1)re+=tt.charAt(ss.indexOf(nowStr));
  		else re+=nowStr;
	}
	return re;
}
//简体转繁体
function Traditionalized(str){
	var re = strS2T()
	return convert(str, re)
}
//繁体转简体
function Simplized(str){
	var re = strT2S()
	return convert(str, re)
}

